      Match.exhaustive
    )),
  // Handle ALL boundary errors
  Effect.catchTag("TransportError", (e) => Console.log(`Transport error: ${e.error.message}`)),
  Effect.catchTag("UnexpectedStatus", (e) => Console.log(`Unexpected status: ${e.status}`)),
